const scriptElement=document.getElementById("blackListPhone");if(scriptElement){const a=scriptElement.getAttribute("src"),b=new URLSearchParams(a.split("?")[1]);inputPhoneId=b.get("blackListPhone")?b.get("inputPhoneId"):"phone"}else console.error("You need add id='blackListPhone' to script");const phoneInputElement=document.getElementById(inputPhoneId);phoneInputElement.addEventListener("blur",function(e){e=e.target.value;"0000000000"===e&&(alert(`Your phone number ${e} is in black list`),location.reload())});